import React, {useState, useEffect} from 'react';
import {SERVER_URL} from '../constants';
import {Link} from 'react-router-dom';

function ListMedia(props) {
    const [medias, setMedia] = useState([]);
    const [message, setMessage] = useState('');

    useEffect(() => {
    // called once after intial render
    fetchMedia();
    }, [] )


    const fetchMedia = () => {
        console.log("fetchMedia");
        fetch(`${SERVER_URL}/api/media`)
        .then((response) => response.json() ) 
        .then((data) => { 
        console.log("assignment length "+data.length);
        setMedia(data);
        }) 
        .catch(err => console.error(err)); 
  };

  const deleteMedia = (id) => {
    fetch(`${SERVER_URL}/api/media/${id}` ,
    {  
      method: 'DELETE'
    } )
.then(res => {
    if (res.ok) {
      //fetchGrades(assignmentId);
      setMessage("Review Deleted.");
      window.location.reload(false);  
    } else {
      setMessage("Save error. "+res.status);
      console.error('Delete review error =' + res.status);
}})
  .catch(err => {
      setMessage("Exception. "+err);
      console.error('Delete review exception =' + err);
  });
};        

  const headers = ['Image', 'Media Title', 'Type', 'Genre', 'Rating', 'Review', ' ', ' '];

    return (
      <div>
        <h3>Reviews</h3>
        <div margin="auto" >
          <h4>{message}&nbsp;</h4>
              <table className="Center"> 
                <thead>
                  <tr>
                    {headers.map((title, idx) => (<th key={idx}>{title}</th>))}
                  </tr>
                </thead>
                <tbody>
                  {medias.map((row, idx) => (
                    <tr key={idx}>
                      <td><img src={row.picURL} height={100} max-width={250}></img></td>
                      <td>{row.title}</td>
                      <td>{row.type}</td>
                      <td>{row.genre}</td>
                      <td>{row.rating}</td>
                      <td>{row.review}</td>
                      <td>
                      <button onClick={()=>deleteMedia(medias[idx].id)}>Delete</button>
                      </td>
                      <td><Link to={`/editReview/${medias[idx].id}`}>Edit</Link></td>

                    </tr>
                  ))}
                </tbody>
              </table>
              <Link to={`/addReview/`}>Add Review</Link>
          </div>
      </div>
    )
}  

export default ListMedia;
